mapper.select:
select

from wp_billings

                               and BID           =  #{bid}             
          and ORDER_SN      =  #{orderSn}         
      and BILLING_SN    =  #{billingSn}       
      and PARTNER_ID    =  #{partnerId}       
                         and BILL_TYPE     =  #{billType}        
      and TRADE_TIME    >= #{startTime}       
          and TRADE_TIME    <= #{endTime}         
        and PAY_STATUS    =  #{payStatus}       

      and TRADE_AMOUNT  >= #{minPayMoney}     
      and TRADE_AMOUNT  <= #{maxPayMoney}     

ORDER BY TRADE_TIME DESC, BID DESC

select SUM(CASE WHEN PAY_STATUS=1 THEN TRADE_AMOUNT ELSE 0 END) AS TRADE_SUCCESS,COUNT(CASE WHEN PAY_STATUS=1 THEN 1 ELSE 0 END) AS SUCCESS_NUM
FROM wp_billings

                               and BID           =  #{bid}             
          and ORDER_SN      =  #{orderSn}         
      and BILLING_SN    =  #{billingSn}       
      and PARTNER_ID    =  #{partnerId}       
                         and BILL_TYPE     =  #{billType}        
      and TRADE_TIME    >= #{startTime}       
          and TRADE_TIME    <= #{endTime}         
        and PAY_STATUS    =  #{payStatus}       
      and TRADE_AMOUNT  >= #{minPayMoney}     
      and TRADE_AMOUNT  <= #{maxPayMoney}
